import { Fragment, useContext } from "react";
import CartContext from "../../../store/cart-context";
import classes from "./MealItem.module.css";
import MealItemForm from "./MealItemForm";
import Card from "../../UI/Card";
import strawberry from "../../../assets/strawberry_1.jpg";
import milk from "../../../assets/milk_1.jpg";

const MealItem = (props) => {
  const cartCtx = useContext(CartContext);
  const price = `$${props.price.toFixed(2)}`;

  const addToCartHandler = (amount) => {
    cartCtx.addItem({
      id: props.id,
      name: props.name,
      amount: amount,
      price: props.price,
    });
  };

  return (
    <Fragment>
      <div className={classes.container}>
        <img src={strawberry} alt="strawberry" className={classes.image}></img>
        <div className={classes.overlay}>
          <div className={classes.text}>Hello World</div>
        </div>
        <div className={classes.meal}>
          <div>
            <h3>{props.name}</h3>
            <div className={classes.description}>{props.description}</div>
            <div className={classes.price}>{price}</div>
            <MealItemForm onAddToCart={addToCartHandler} />
          </div>
        </div>
      </div>
      <div className={classes.container}>
        <img src={milk} alt="milk" className={classes.image}></img>
        <div className={classes.overlay}>
          <div className={classes.text}>Hello World</div>
        </div>
        <div className={classes.meal}>
          <div>
            <h3>{props.name}</h3>
            <div className={classes.description}>{props.description}</div>
            <div className={classes.price}>{price}</div>
            <MealItemForm onAddToCart={addToCartHandler} />
          </div>
        </div>
      </div>
    </Fragment>
  );
};

export default MealItem;
