import { useState, useRef } from "react";

import Header from "./components/Layout/Header";
import Cart from "./components/Cart/Cart";
import CartProvider from "./store/CartProvider";
import AvailableMeals from "./components/Meals/AvailableMeals";
import SocialFollow from "./components/Layout/SocialFollow";

function App() {
  const cocktailRef = useRef();

  const [cartIsShown, setCartIsShown] = useState(false);

  const showCartHandler = () => {
    setCartIsShown(true);
  };

  const hideCartHandler = () => {
    setCartIsShown(false);
  };

  const downClickHandler = () => {
    cocktailRef.current.scrollIntoView({ behavior: "smooth" });
  };
  return (
    <CartProvider>
      {cartIsShown && <Cart onClose={hideCartHandler} />}
      <Header onShownCart={showCartHandler} onDownClick={downClickHandler} />
      <main>
        <AvailableMeals ref={cocktailRef} />
      </main>
      <SocialFollow />
    </CartProvider>
  );
}

export default App;
